;(function (window, document, undefined) {

    var Modernizr = function () {};

    Modernizr.prototype = {
        // Detect various features
        flexbox: function () {
            var a = document.createElement("div"),
                b = document.createElement("div");
            a.style.cssText = "display:box;";
            b.style.cssText = "box-flex:1;width:10px;";
            a.appendChild(b);
            document.documentElement.appendChild(a);
            var c = b.offsetWidth === 42;
            a.removeChild(b);
            document.documentElement.removeChild(a);
            return c;
        },
        canvas: function () {
            return !!document.createElement("canvas").getContext;
        },
        // ... more feature detections here ...
    };

    // Create a new instance of Modernizr
    var modernizr = new Modernizr();

    // Export the Modernizr object to the global scope
    window.Modernizr = modernizr;

})(window, document);
